CREATE TABLE tree
	(
		id INT AUTO_INCREMENT PRIMARY KEY,
		botanical_name VARCHAR(255) UNIQUE NOT NULL,
        somali_name VARCHAR(255),
        arbic_name VARCHAR(255),
        english_name VARCHAR(255),
        tree_type VARCHAR(255)
    );

CREATE TABLE regional_spelling 
	(
		id INT AUTO_INCREMENT PRIMARY KEY,
        spelling VARCHAR(255),
        tree_id INT,
        FOREIGN KEY(tree_id) REFERENCES tree(id)
	);
    
CREATE TABLE climatic
	(
		id INT PRIMARY KEY,
        climatic_zone VARCHAR(50),
        rainfall_min INT,
        rainfall_max INT,
        altitude_min INT,
        altitude_max INT,
        temperature_min INT,
        temperature_max INT 
	);
 
CREATE TABLE climatic_zone
	(
		id INT AUTO_INCREMENT PRIMARY KEY,
        climatic_id INT,
        tree_id INT,
        FOREIGN KEY(climatic_id) REFERENCES climatic(id),
        FOREIGN KEY(tree_id) REFERENCES tree(id)
    )
